-- 中文公共模板库
-- 为用户提供更多实用的中文模板

-- 1. 学生通知书（中文版）
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('学生通知书', 'student_management', '学生各类通知书模板，适用于成绩通知、活动通知等',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 600px; margin: 0 auto;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">{{noticeTitle}}</h2><div style=\"margin-bottom: 20px; line-height: 1.8;\"><p><strong>尊敬的{{parentName}}家长：</strong></p><p style=\"text-indent: 2em;\">您好！现将您的孩子{{studentName}}（{{className}}班）的相关情况通知如下：</p><div style=\"background: #f8f9fa; padding: 15px; margin: 20px 0; border-left: 4px solid #007bff;\"><p style=\"margin: 0; text-indent: 2em;\">{{noticeContent}}</p></div><p style=\"text-indent: 2em;\">{{additionalInfo}}</p><p style=\"text-indent: 2em;\">如有疑问，请及时与班主任联系。谢谢您的配合！</p></div><div style=\"text-align: right; margin-top: 40px;\"><p>{{schoolName}}</p><p>班主任：{{teacherName}}</p><p>联系电话：{{teacherPhone}}</p><p>{{noticeDate}}</p></div></div>"}',
'{"noticeTitle": {"label": "通知标题", "type": "text", "required": true, "placeholder": "如：期中考试成绩通知"}, "parentName": {"label": "家长姓名", "type": "text", "required": true}, "studentName": {"label": "学生姓名", "type": "text", "required": true}, "className": {"label": "班级名称", "type": "text", "required": true}, "noticeContent": {"label": "通知内容", "type": "textarea", "required": true, "placeholder": "请详细描述通知的主要内容"}, "additionalInfo": {"label": "补充说明", "type": "textarea", "placeholder": "其他需要说明的事项（可选）"}, "schoolName": {"label": "学校名称", "type": "text", "required": true}, "teacherName": {"label": "班主任姓名", "type": "text", "required": true}, "teacherPhone": {"label": "联系电话", "type": "text", "required": true}, "noticeDate": {"label": "通知日期", "type": "date", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 2. 家长会通知（中文版）
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('家长会通知', 'parent_communication', '家长会召开通知模板，包含时间地点和注意事项',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 600px; margin: 0 auto;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #e74c3c;\">家长会通知</h2><div style=\"margin-bottom: 20px; line-height: 1.8;\"><p><strong>尊敬的{{className}}班家长：</strong></p><p style=\"text-indent: 2em;\">您好！为了加强家校沟通，共同关注孩子的成长，我校定于<strong>{{meetingDate}}</strong>召开家长会。</p><div style=\"background: #fff3cd; padding: 15px; margin: 20px 0; border: 1px solid #ffeaa7; border-radius: 5px;\"><h4 style=\"color: #856404; margin-top: 0;\">会议安排</h4><p><strong>时间：</strong>{{meetingDate}} {{meetingTime}}</p><p><strong>地点：</strong>{{meetingLocation}}</p><p><strong>主题：</strong>{{meetingTopic}}</p></div><div style=\"background: #d1ecf1; padding: 15px; margin: 20px 0; border: 1px solid #bee5eb; border-radius: 5px;\"><h4 style=\"color: #0c5460; margin-top: 0;\">会议内容</h4><p>{{meetingContent}}</p></div><div style=\"background: #f8d7da; padding: 15px; margin: 20px 0; border: 1px solid #f5c6cb; border-radius: 5px;\"><h4 style=\"color: #721c24; margin-top: 0;\">注意事项</h4><p>{{meetingNotes}}</p></div><p style=\"text-indent: 2em;\">请您务必准时参加，如有特殊情况不能参加，请提前与班主任联系。</p></div><div style=\"text-align: right; margin-top: 40px;\"><p>{{schoolName}}</p><p>{{className}}班主任：{{teacherName}}</p><p>{{noticeDate}}</p></div></div>"}',
'{"className": {"label": "班级名称", "type": "text", "required": true}, "meetingDate": {"label": "会议日期", "type": "date", "required": true}, "meetingTime": {"label": "会议时间", "type": "time", "required": true}, "meetingLocation": {"label": "会议地点", "type": "text", "required": true}, "meetingTopic": {"label": "会议主题", "type": "text", "required": true}, "meetingContent": {"label": "会议内容", "type": "textarea", "required": true}, "meetingNotes": {"label": "注意事项", "type": "textarea", "required": true}, "schoolName": {"label": "学校名称", "type": "text", "required": true}, "teacherName": {"label": "班主任姓名", "type": "text", "required": true}, "noticeDate": {"label": "通知日期", "type": "date", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 3. 班级通讯录
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('班级通讯录', 'class_management', '班级学生和家长联系方式汇总表',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">{{className}} 通讯录</h2><div style=\"margin-bottom: 20px;\"><p><strong>班主任：</strong>{{teacherName}} &nbsp;&nbsp; <strong>电话：</strong>{{teacherPhone}}</p><p><strong>学期：</strong>{{semester}} &nbsp;&nbsp; <strong>制表日期：</strong>{{createDate}}</p></div><table style=\"width: 100%; border-collapse: collapse; margin-top: 20px;\"><thead><tr style=\"background-color: #f8f9fa;\"><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">序号</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">学生姓名</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">学号</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">家长姓名</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">联系电话</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">备注</th></tr></thead><tbody>{{studentList}}</tbody></table><div style=\"margin-top: 30px; font-size: 12px; color: #666;\"><p>注：请各位家长核对联系方式，如有变更请及时告知班主任。</p></div></div>"}',
'{"className": {"label": "班级名称", "type": "text", "required": true}, "teacherName": {"label": "班主任姓名", "type": "text", "required": true}, "teacherPhone": {"label": "班主任电话", "type": "text", "required": true}, "semester": {"label": "学期", "type": "text", "required": true}, "createDate": {"label": "制表日期", "type": "date", "required": true}, "studentList": {"label": "学生信息列表", "type": "textarea", "required": true, "placeholder": "请输入学生信息，格式：序号|学生姓名|学号|家长姓名|联系电话|备注，每行一个学生"}}',
'pdf,word,html', 1, datetime('now'));

-- 4. 学生请假条（中文版）
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('学生请假条', 'student_management', '学生请假申请表模板',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 500px; margin: 0 auto; border: 2px solid #333;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">学生请假条</h2><div style=\"line-height: 2; margin-bottom: 20px;\"><p><strong>班级：</strong>{{className}} &nbsp;&nbsp;&nbsp;&nbsp; <strong>姓名：</strong>{{studentName}}</p><p><strong>学号：</strong>{{studentId}} &nbsp;&nbsp;&nbsp;&nbsp; <strong>申请日期：</strong>{{applyDate}}</p></div><div style=\"margin: 20px 0;\"><p><strong>请假事由：</strong></p><div style=\"border: 1px solid #ddd; padding: 10px; min-height: 60px; margin-top: 10px;\">{{reason}}</div></div><div style=\"margin: 20px 0;\"><p><strong>请假时间：</strong>{{startDate}} 至 {{endDate}} （共{{days}}天）</p></div><div style=\"margin: 20px 0;\"><p><strong>家长联系电话：</strong>{{parentPhone}}</p></div><div style=\"display: flex; justify-content: space-between; margin-top: 40px;\"><div style=\"text-align: center;\"><p>学生签名：</p><p style=\"margin-top: 20px;\">_____________</p></div><div style=\"text-align: center;\"><p>家长签名：</p><p style=\"margin-top: 20px;\">_____________</p></div><div style=\"text-align: center;\"><p>班主任签名：</p><p style=\"margin-top: 20px;\">_____________</p></div></div></div>"}',
'{"className": {"label": "班级名称", "type": "text", "required": true}, "studentName": {"label": "学生姓名", "type": "text", "required": true}, "studentId": {"label": "学号", "type": "text", "required": true}, "applyDate": {"label": "申请日期", "type": "date", "required": true}, "reason": {"label": "请假事由", "type": "textarea", "required": true}, "startDate": {"label": "开始日期", "type": "date", "required": true}, "endDate": {"label": "结束日期", "type": "date", "required": true}, "days": {"label": "请假天数", "type": "number", "required": true}, "parentPhone": {"label": "家长电话", "type": "text", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 5. 班级活动方案（中文版）
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('班级活动方案', 'activity_management', '班级活动策划方案模板',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 800px; margin: 0 auto;\"><h1 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">{{activityTitle}}</h1><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #3498db; border-bottom: 2px solid #3498db; padding-bottom: 5px;\">活动基本信息</h3><div style=\"background: #f8f9fa; padding: 15px; margin: 10px 0;\"><p><strong>活动主题：</strong>{{activityTitle}}</p><p><strong>活动时间：</strong>{{activityDate}} {{activityTime}}</p><p><strong>活动地点：</strong>{{activityLocation}}</p><p><strong>参与班级：</strong>{{className}}</p><p><strong>负责老师：</strong>{{teacherName}}</p></div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #e74c3c; border-bottom: 2px solid #e74c3c; padding-bottom: 5px;\">活动目的</h3><div style=\"padding: 15px; border-left: 4px solid #e74c3c;\">{{activityPurpose}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #f39c12; border-bottom: 2px solid #f39c12; padding-bottom: 5px;\">活动流程</h3><div style=\"padding: 15px; border-left: 4px solid #f39c12;\">{{activityProcess}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #27ae60; border-bottom: 2px solid #27ae60; padding-bottom: 5px;\">物资准备</h3><div style=\"padding: 15px; border-left: 4px solid #27ae60;\">{{materials}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #9b59b6; border-bottom: 2px solid #9b59b6; padding-bottom: 5px;\">注意事项</h3><div style=\"padding: 15px; border-left: 4px solid #9b59b6;\">{{notes}}</div></div><div style=\"text-align: right; margin-top: 40px;\"><p>策划人：{{plannerName}}</p><p>策划日期：{{planDate}}</p></div></div>"}',
'{"activityTitle": {"label": "活动标题", "type": "text", "required": true}, "activityDate": {"label": "活动日期", "type": "date", "required": true}, "activityTime": {"label": "活动时间", "type": "time", "required": true}, "activityLocation": {"label": "活动地点", "type": "text", "required": true}, "className": {"label": "班级名称", "type": "text", "required": true}, "teacherName": {"label": "负责老师", "type": "text", "required": true}, "activityPurpose": {"label": "活动目的", "type": "textarea", "required": true}, "activityProcess": {"label": "活动流程", "type": "textarea", "required": true}, "materials": {"label": "物资准备", "type": "textarea", "required": true}, "notes": {"label": "注意事项", "type": "textarea", "required": true}, "plannerName": {"label": "策划人", "type": "text", "required": true}, "planDate": {"label": "策划日期", "type": "date", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 6. 成绩通知单（中文版）
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('成绩通知单', 'grade_management', '学生成绩通知单模板',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 600px; margin: 0 auto;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">成绩通知单</h2><div style=\"margin-bottom: 20px;\"><p><strong>学生姓名：</strong>{{studentName}} &nbsp;&nbsp; <strong>班级：</strong>{{className}}</p><p><strong>学号：</strong>{{studentId}} &nbsp;&nbsp; <strong>考试名称：</strong>{{examName}}</p><p><strong>考试时间：</strong>{{examDate}}</p></div><table style=\"width: 100%; border-collapse: collapse; margin: 20px 0;\"><thead><tr style=\"background-color: #f8f9fa;\"><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">科目</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">成绩</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">等级</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">备注</th></tr></thead><tbody><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">语文</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{chineseScore}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{chineseGrade}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{chineseNote}}</td></tr><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">数学</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mathScore}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mathGrade}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mathNote}}</td></tr><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">英语</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{englishScore}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{englishGrade}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{englishNote}}</td></tr></tbody></table><div style=\"margin: 20px 0;\"><p><strong>总分：</strong>{{totalScore}} &nbsp;&nbsp; <strong>班级排名：</strong>{{classRank}}</p></div><div style=\"background: #f8f9fa; padding: 15px; margin: 20px 0; border-left: 4px solid #007bff;\"><p><strong>老师评语：</strong></p><p style=\"margin-top: 10px;\">{{teacherComment}}</p></div><div style=\"text-align: right; margin-top: 40px;\"><p>班主任：{{teacherName}}</p><p>{{noticeDate}}</p></div></div>"}',
'{"studentName": {"label": "学生姓名", "type": "text", "required": true}, "className": {"label": "班级名称", "type": "text", "required": true}, "studentId": {"label": "学号", "type": "text", "required": true}, "examName": {"label": "考试名称", "type": "text", "required": true}, "examDate": {"label": "考试时间", "type": "date", "required": true}, "chineseScore": {"label": "语文成绩", "type": "number"}, "chineseGrade": {"label": "语文等级", "type": "text"}, "chineseNote": {"label": "语文备注", "type": "text"}, "mathScore": {"label": "数学成绩", "type": "number"}, "mathGrade": {"label": "数学等级", "type": "text"}, "mathNote": {"label": "数学备注", "type": "text"}, "englishScore": {"label": "英语成绩", "type": "number"}, "englishGrade": {"label": "英语等级", "type": "text"}, "englishNote": {"label": "英语备注", "type": "text"}, "totalScore": {"label": "总分", "type": "number"}, "classRank": {"label": "班级排名", "type": "number"}, "teacherComment": {"label": "老师评语", "type": "textarea"}, "teacherName": {"label": "班主任姓名", "type": "text", "required": true}, "noticeDate": {"label": "通知日期", "type": "date", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 7. 课程表模板
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('班级课程表', 'class_management', '班级课程表模板，可自定义课程安排',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px;\"><h2 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">{{className}} 课程表</h2><div style=\"margin-bottom: 20px;\"><p><strong>学期：</strong>{{semester}} &nbsp;&nbsp; <strong>班主任：</strong>{{teacherName}}</p></div><table style=\"width: 100%; border-collapse: collapse; margin: 20px 0;\"><thead><tr style=\"background-color: #f8f9fa;\"><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">时间\\星期</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">周一</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">周二</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">周三</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">周四</th><th style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">周五</th></tr></thead><tbody><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center; font-weight: bold;\">第一节<br>{{period1Time}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mon1}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{tue1}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{wed1}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{thu1}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{fri1}}</td></tr><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center; font-weight: bold;\">第二节<br>{{period2Time}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mon2}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{tue2}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{wed2}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{thu2}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{fri2}}</td></tr><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center; font-weight: bold;\">第三节<br>{{period3Time}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mon3}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{tue3}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{wed3}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{thu3}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{fri3}}</td></tr><tr><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center; font-weight: bold;\">第四节<br>{{period4Time}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{mon4}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{tue4}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{wed4}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{thu4}}</td><td style=\"border: 1px solid #ddd; padding: 8px; text-align: center;\">{{fri4}}</td></tr></tbody></table><div style=\"margin-top: 30px; font-size: 12px; color: #666;\"><p>制表日期：{{createDate}}</p></div></div>"}',
'{"className": {"label": "班级名称", "type": "text", "required": true}, "semester": {"label": "学期", "type": "text", "required": true}, "teacherName": {"label": "班主任", "type": "text", "required": true}, "period1Time": {"label": "第一节时间", "type": "text", "required": true, "placeholder": "如：8:00-8:45"}, "period2Time": {"label": "第二节时间", "type": "text", "required": true, "placeholder": "如：8:55-9:40"}, "period3Time": {"label": "第三节时间", "type": "text", "required": true, "placeholder": "如：10:00-10:45"}, "period4Time": {"label": "第四节时间", "type": "text", "required": true, "placeholder": "如：10:55-11:40"}, "mon1": {"label": "周一第一节", "type": "text"}, "tue1": {"label": "周二第一节", "type": "text"}, "wed1": {"label": "周三第一节", "type": "text"}, "thu1": {"label": "周四第一节", "type": "text"}, "fri1": {"label": "周五第一节", "type": "text"}, "mon2": {"label": "周一第二节", "type": "text"}, "tue2": {"label": "周二第二节", "type": "text"}, "wed2": {"label": "周三第二节", "type": "text"}, "thu2": {"label": "周四第二节", "type": "text"}, "fri2": {"label": "周五第二节", "type": "text"}, "mon3": {"label": "周一第三节", "type": "text"}, "tue3": {"label": "周二第三节", "type": "text"}, "wed3": {"label": "周三第三节", "type": "text"}, "thu3": {"label": "周四第三节", "type": "text"}, "fri3": {"label": "周五第三节", "type": "text"}, "mon4": {"label": "周一第四节", "type": "text"}, "tue4": {"label": "周二第四节", "type": "text"}, "wed4": {"label": "周三第四节", "type": "text"}, "thu4": {"label": "周四第四节", "type": "text"}, "fri4": {"label": "周五第四节", "type": "text"}, "createDate": {"label": "制表日期", "type": "date", "required": true}}',
'pdf,word,html', 1, datetime('now'));

-- 8. 教学计划模板
INSERT INTO document_templates (name, category, description, template_content, fields, file_type, is_system, created_at) VALUES
('教学计划', 'teaching_management', '学期教学计划模板',
'{"type": "html", "content": "<div style=\"font-family: SimSun; padding: 20px; max-width: 800px; margin: 0 auto;\"><h1 style=\"text-align: center; margin-bottom: 30px; color: #2c3e50;\">{{subject}}教学计划</h1><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #3498db; border-bottom: 2px solid #3498db; padding-bottom: 5px;\">基本信息</h3><div style=\"background: #f8f9fa; padding: 15px; margin: 10px 0;\"><p><strong>学科：</strong>{{subject}} &nbsp;&nbsp; <strong>年级：</strong>{{grade}} &nbsp;&nbsp; <strong>班级：</strong>{{className}}</p><p><strong>任课教师：</strong>{{teacherName}} &nbsp;&nbsp; <strong>学期：</strong>{{semester}}</p><p><strong>制定日期：</strong>{{planDate}}</p></div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #e74c3c; border-bottom: 2px solid #e74c3c; padding-bottom: 5px;\">教学目标</h3><div style=\"padding: 15px; border-left: 4px solid #e74c3c;\">{{teachingGoals}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #f39c12; border-bottom: 2px solid #f39c12; padding-bottom: 5px;\">教学内容</h3><div style=\"padding: 15px; border-left: 4px solid #f39c12;\">{{teachingContent}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #27ae60; border-bottom: 2px solid #27ae60; padding-bottom: 5px;\">教学方法</h3><div style=\"padding: 15px; border-left: 4px solid #27ae60;\">{{teachingMethods}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #9b59b6; border-bottom: 2px solid #9b59b6; padding-bottom: 5px;\">教学进度安排</h3><div style=\"padding: 15px; border-left: 4px solid #9b59b6;\">{{teachingSchedule}}</div></div><div style=\"margin-bottom: 30px;\"><h3 style=\"color: #34495e; border-bottom: 2px solid #34495e; padding-bottom: 5px;\">评价方式</h3><div style=\"padding: 15px; border-left: 4px solid #34495e;\">{{evaluationMethods}}</div></div></div>"}',
'{"subject": {"label": "学科名称", "type": "text", "required": true}, "grade": {"label": "年级", "type": "text", "required": true}, "className": {"label": "班级", "type": "text", "required": true}, "teacherName": {"label": "任课教师", "type": "text", "required": true}, "semester": {"label": "学期", "type": "text", "required": true}, "planDate": {"label": "制定日期", "type": "date", "required": true}, "teachingGoals": {"label": "教学目标", "type": "textarea", "required": true}, "teachingContent": {"label": "教学内容", "type": "textarea", "required": true}, "teachingMethods": {"label": "教学方法", "type": "textarea", "required": true}, "teachingSchedule": {"label": "教学进度安排", "type": "textarea", "required": true}, "evaluationMethods": {"label": "评价方式", "type": "textarea", "required": true}}',
'pdf,word,html', 1, datetime('now'));